        "month": "dec",
        "number": "12",
        "pages": "1973--1982",
        "pmid": "26356911",
        "title": "Reinforcing Visual Grouping Cues to Communicate Complex Informational Structure",
        "type": "article",
        "volume": "20",
        "year": "2014"
